def calculate_covariance_matrix(vectors: list[list[float]]) -> list[list[float]]:
    if not vectors:
        return []
    n = len(vectors[0])
    m = len(vectors)
    if n == 0:
        return []
    means = [sum(vec) / n for vec in vectors]
    cov_matrix = [[0.0 for _ in range(m)] for _ in range(m)]
    for i in range(m):
        for j in range(m):
            covariance = 0.0
            for k in range(n):
                covariance += (vectors[i][k] - means[i]) * (vectors[j][k] - means[j])
            covariance /= n - 1
            cov_matrix[i][j] = covariance
    return cov_matrix
