def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(m: list[list[int | float]]) -> float:
        (a, b, c) = m[0]
        (d, e, f) = m[1]
        (g, h, i) = m[2]
        return a * (e * i - f * h) - b * (d * i - f * g) + c * (d * h - e * g)
    det = 0.0
    for col in range(4):
        minor = []
        for r in range(1, 4):
            row = []
            for c in range(4):
                if c != col:
                    row.append(matrix[r][c])
            minor.append(row)
        sign = (-1) ** col
        minor_det = determinant_3x3(minor)
        det += matrix[0][col] * sign * minor_det
    return det
