class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None

    def initialize(self):
        if self.input_shape is None:
            raise ValueError('Input shape must be set before initializing the layer.')
        limit = 1 / np.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, (self.input_shape[0], self.n_units))
        self.w0 = np.zeros((self.n_units,))
        self.optimizer_W = Optimizer()
        self.optimizer_w0 = Optimizer()

    def parameters(self):
        if self.W is None or self.w0 is None:
            return 0
        return self.W.size + self.w0.size

    def forward_pass(self, X, training):
        self.layer_input = X
        output = X.dot(self.W) + self.w0
        return output

    def backward_pass(self, accum_grad):
        if self.trainable:
            grad_W = self.layer_input.T.dot(accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0)
            self.optimizer_W.update(self.W, grad_W)
            self.optimizer_w0.update(self.w0, grad_w0)
        dX = accum_grad.dot(self.W.T)
        return dX

    def output_shape(self):
        return (self.n_units,)
