def recall(y_true, y_pred):
    TP = 0
    FN = 0
    for (yt, yp) in zip(y_true, y_pred):
        if yt == 1:
            if yp == 1:
                TP += 1
            else:
                FN += 1
    denominator = TP + FN
    if denominator == 0:
        return 0.0
    else:
        rec = TP / denominator
        return round(rec, 3)
