def gauss_seidel(A, b, n, x_ini=None):
    m = A.shape[0]
    if x_ini is None:
        x = np.zeros(m, dtype=np.float64)
    else:
        x = np.asarray(x_ini, dtype=np.float64)
    for _ in range(n):
        for i in range(m):
            s = 0.0
            for j in range(m):
                if j != i:
                    s += A[i, j] * x[j]
            x[i] = (b[i] - s) / A[i, i]
    x = np.round(x, 4)
    return x.tolist()
