def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    df_dict = {}
    for w in query:
        count = 0
        for doc in corpus:
            if w in doc:
                count += 1
        df_dict[w] = count
    N = len(corpus)
    idf_dict = {}
    for w in query:
        df = df_dict[w]
        idf = np.log((N + 1) / (df + 1))
        idf_dict[w] = idf
    result = []
    for doc in corpus:
        if not doc:
            scores = [0.0 for _ in query]
        else:
            scores = []
            for w in query:
                count = doc.count(w)
                tf = count / len(doc)
                tf_idf = tf * idf_dict[w]
                scores.append(round(tf_idf, 5))
        result.append(scores)
    return result
