def calculate_brightness(img):
    if not img:
        return -1
    num_rows = len(img)
    num_cols = len(img[0])
    if num_cols == 0:
        return -1
    for row in img:
        if len(row) != num_cols:
            return -1
    for row in img:
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
    total = 0
    for row in img:
        total += sum(row)
    average = total / (num_rows * num_cols)
    return round(average, 2)
