            return ' '.join([reversed_dict.get(tok_id, '<UNK>') for tok_id in token_ids])
    hparams = {'n_ctx': 1024, 'n_head': 12}
    params = {'wte': np.random.rand(3, 10), 'wpe': np.random.rand(1024, 10), 'blocks': [], 'ln_f': {'g': np.ones(10), 'b': np.zeros(10)}}
    encoder = DummyBPE()
    return (encoder, hparams, params)
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    generated_tokens = encoder.encode(prompt)
    for _ in range(n_tokens_to_generate):
        token_embeddings = params['wte'][generated_tokens]
        pos_embeddings = params['wpe'][:len(generated_tokens)]
        embeddings = token_embeddings + pos_embeddings
        mean = np.mean(embeddings, axis=-1, keepdims=True)
        std = np.std(embeddings, axis=-1, keepdims=True)
        normalized = (embeddings - mean) / (std + 1e-05)
        normalized = normalized * params['ln_f']['g'] + params['ln_f']['b']
        last_embedding = normalized[-1]
        logits = np.dot(last_embedding, params['wte'].T)
        probs = np.exp(logits) / np.sum(np.exp(logits))
        next_token = np.random.choice(len(probs), p=probs)
        generated_tokens.append(next_token)
    generated_text = encoder.decode(generated_tokens)
    return generated_text
