def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a or not b:
        return -1
    n_cols = len(a[0])
    if n_cols != len(b):
        return -1
    for row in a:
        if len(row) != n_cols:
            return -1
    result = []
    for row in a:
        dot_product = 0.0
        for i in range(n_cols):
            dot_product += row[i] * b[i]
        result.append(dot_product)
    return result
