def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if mode == 'row':
        means = []
        for row in matrix:
            mean = sum(row) / len(row)
            means.append(mean)
        return means
    elif mode == 'column':
        if not matrix:
            return []
        num_rows = len(matrix)
        num_cols = len(matrix[0]) if num_rows > 0 else 0
        means = []
        for col in range(num_cols):
            total = 0.0
            for row in matrix:
                total += row[col]
            mean = total / num_rows
            means.append(mean)
        return means
    else:
        raise ValueError('Invalid mode provided.')
