def transform_matrix(A: list[list[int | float]], T: list[list[int | float]], S: list[list[int | float]]) -> list[list[int | float]]:
    try:
        t_matrix = np.array(T)
        s_matrix = np.array(S)
        a_matrix = np.array(A)
    except:
        return -1
    if t_matrix.shape[0] != t_matrix.shape[1]:
        return -1
    try:
        det_t = np.linalg.det(t_matrix)
    except np.linalg.LinAlgError:
        return -1
    if abs(det_t) < 1e-06:
        return -1
    try:
        t_inv = np.linalg.inv(t_matrix)
    except np.linalg.LinAlgError:
        return -1
    if a_matrix.shape[0] != t_matrix.shape[0]:
        return -1
    if a_matrix.shape[1] != s_matrix.shape[0]:
        return -1
    try:
        result1 = np.dot(t_inv, a_matrix)
    except:
        return -1
    try:
        result2 = np.dot(result1, s_matrix)
    except:
        return -1
    result_list = result2.tolist()
    rounded_result = []
    for row in result_list:
        rounded_row = [round(x, 4) for x in row]
        rounded_result.append(rounded_row)
    return rounded_result
