def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not b:
        return -1
    n_cols_a = len(a[0])
    for row in a:
        if len(row) != n_cols_a:
            return -1
    n_cols_b = len(b[0])
    for row in b:
        if len(row) != n_cols_b:
            return -1
    if n_cols_a != len(b):
        return -1
    result = []
    for i in range(len(a)):
        new_row = []
        for j in range(len(b[0])):
            sum_val = 0.0
            for k in range(len(a[i])):
                sum_val += a[i][k] * b[k][j]
            new_row.append(sum_val)
        result.append(new_row)
    return result
