def calculate_covariance_matrix(vectors: list[list[float]]) -> list[list[float]]:
    if not vectors:
        return []
    n = len(vectors)
    if n == 0:
        return []
    n_features = len(vectors[0])
    means = [sum((v[i] for v in vectors)) / n for i in range(n_features)]
    cov_matrix = []
    for i in range(n_features):
        row = []
        for j in range(n_features):
            if i == j:
                sum_sq_diff = sum(((x - means[i]) ** 2 for x in vectors[i]))
                if n == 1:
                    var = 0.0
                else:
                    var = sum_sq_diff / (n - 1)
                row.append(var)
            else:
                sum_product = 0.0
                for k in range(n):
                    x_i = vectors[k][i]
                    x_j = vectors[k][j]
                    sum_product += (x_i - means[i]) * (x_j - means[j])
                if n == 1:
                    cov = 0.0
                else:
                    cov = sum_product / (n - 1)
                row.append(cov)
        cov_matrix.append(row)
    return cov_matrix
