def rref(matrix):
    if isinstance(matrix, list):
        matrix = np.array(matrix)
    matrix = matrix.copy()
    n_rows = matrix.shape[0]
    if n_rows == 0:
        return []
    n_cols = matrix.shape[1]
    for row in range(n_rows):
        pivot_col = None
        for col in range(n_cols):
            if abs(matrix[row, col]) > 1e-10:
                pivot_col = col
                break
        if pivot_col is None:
            continue
        max_row = row
        max_val = abs(matrix[row, pivot_col])
        for r in range(row + 1, n_rows):
            if abs(matrix[r, pivot_col]) > max_val:
                max_val = abs(matrix[r, pivot_col])
                max_row = r
        if max_row != row:
            (matrix[row], matrix[max_row]) = (matrix[max_row], matrix[row])
        for r in range(row + 1, n_rows):
            factor = matrix[r, pivot_col] / matrix[row, pivot_col]
            matrix[r] = matrix[r] - factor * matrix[row]
        matrix[row, pivot_col] = 1
    for row in reversed(range(n_rows)):
        pivot_col = None
        for col in range(n_cols):
            if abs(matrix[row, col]) > 1e-10:
                pivot_col = col
                break
        if pivot_col is None:
            continue
        for r in range(row - 1, -1, -1):
            factor = matrix[r, pivot_col] / matrix[row, pivot_col]
            matrix[r] = matrix[r] - factor * matrix[row]
        matrix[row, pivot_col] = 1
    return matrix.tolist()
