def gauss_seidel(A, b, n, x_ini=None):
    if x_ini is None:
        x = np.zeros(len(b))
    else:
        x = np.array(x_ini)
    for _ in range(n):
        for i in range(len(x)):
            sum_val = np.dot(A[i], x)
            x_i = (b[i] - sum_val) / A[i][i]
            x[i] = x_i
    x_rounded = np.round(x, 4)
    return x_rounded.tolist()
