def calculate_brightness(img):
    if not img:
        return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
    total = sum((pixel for row in img for pixel in row))
    total_pixels = len(img) * len(img[0])
    if total_pixels == 0:
        return -1
    average = total / total_pixels
    return round(average, 2)
