def performance_metrics(actual: list[int], predicted: list[int]) -> tuple:
    counts = Counter(zip(actual, predicted))
    tp = counts.get((1, 1), 0)
    fp = counts.get((0, 1), 0)
    fn = counts.get((1, 0), 0)
    tn = counts.get((0, 0), 0)
    confusion_matrix = [[tn, fp], [fn, tp]]
    total = len(actual)
    if total == 0:
        return (confusion_matrix, 0.0, 0.0, 0.0, 0.0)
    accuracy = (tp + tn) / total
    if tp + fp != 0:
        precision = tp / (tp + fp)
    else:
        precision = 0.0
    if tp + fn != 0:
        recall = tp / (tp + fn)
    else:
        recall = 0.0
    if precision + recall != 0:
        f1 = 2 * (precision * recall) / (precision + recall)
    else:
        f1 = 0.0
    if tn + fn != 0:
        specificity = tn / (tn + fn)
        negative_predictive_value = tn / (tn + fn)
    else:
        specificity = 0.0
        negative_predictive_value = 0.0
    accuracy = round(accuracy, 3)
    f1 = round(f1, 3)
    specificity = round(specificity, 3)
    negative_predictive_value = round(negative_predictive_value, 3)
    return (confusion_matrix, accuracy, f1, specificity, negative_predictive_value)
