def gen_text(prompt: str, n_tokens_to_generate: int=40):
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    encoded_prompt = encoder.encode(prompt)
    context = encoded_prompt
    generated = []
    for _ in range(n_tokens_to_generate):
        if not context:
            new_token = np.random.randint(params['wte'].shape[1])
            generated_token_embedding = params['wte'][new_token]
        else:
            window_size = hparams['n_ctx']
            attention_scores = compute_attention_scores(context, window_size)
            attention_output = apply_multi_head_attention(attention_scores, context, params)
            ff_output = apply_feed_forward(attention_output, params)
            normalized_output = apply_layer_norm(ff_output, params['ln_f'])
            new_token = np.argmax(normalized_output)
            generated_token_embedding = params['wte'][new_token]
        generated.append(new_token)
        context = context[1:] + [generated_token_embedding]
        if len(context) > hparams['n_ctx']:
            context = context[-hparams['n_ctx']:]
    generated_text = encoder.decode(generated)
    return generated_text
