from collections import Counter
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    query_terms = query.split()
    if not query_terms:
        return [0.0]
    global_freq = {}
    for doc in corpus:
        terms = doc.split()
        unique_terms = set(terms)
        for term in unique_terms:
            if term in global_freq:
                global_freq[term] += 1
            else:
                global_freq[term] = 1
    scores = []
    total_documents = len(corpus)
    for doc in corpus:
        terms = doc.split()
        count = Counter(terms)
        total_words = len(terms)
        score_sum = 0.0
        for term in query_terms:
            if term in count:
                tf = (k1 + 1) * count[term] / total_words
                if tf > 1:
                    tf = 1.0
                idf = global_freq[term] / total_documents
                score_sum += tf * idf
        if total_words == 0:
            norm = 1.0
        else:
            norm = (1 + total_words * (1 - b)) ** (-b)
        score = score_sum * norm
        scores.append(round(score, 3))
    return scores
assert calculate_bm25_scores([['the', 'cat', 'sat'], ['the', 'dog', 'ran'], ['the', 'bird', 'flew']], ['the', 'cat']) == [0.693, 0., 0. ]
