class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        super().__init__()
        self.set_input_shape(input_shape)
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = self._create_optimizer(0.01)
        self.optimizer_w0 = self._create_optimizer(0.01)

    def _create_optimizer(self, lr=0.01):

        def optimizer(param, grad):
            param -= lr * grad
            return param
        return optimizer

    def initialize(self):
        if self.input_shape is None:
            raise ValueError('Input shape must be provided.')
        n_input = self.input_shape[0]
        self.W = np.random.uniform(-1.0 / np.sqrt(n_input), 1.0 / np.sqrt(n_input), (n_input, self.n_units))
        self.w0 = np.zeros(self.n_units)

    def parameters(self):
        if self.input_shape is None:
            raise ValueError('Input shape must be provided.')
        n_input = self.input_shape[0]
        n_units = self.n_units
        return n_input * n_units + n_units

    def forward_pass(self, X, training):
        if not self.trainable:
            return X
        output = X.dot(self.W) + self.w0
        return output

