def gradient_descent(X, y, weights, learning_rate, n_iterations, batch_size=1, method='batch'):
    N = X.shape[0]
    for _ in range(n_iterations):
        y_pred = np.dot(X, weights)
        error = y_pred - y
        full_gradient = np.dot(X.T, error) / N
        if method == 'sgd':
            i = np.random.randint(N)
            sample_gradient = full_gradient[i, :]
            weights -= learning_rate * sample_gradient
        elif method == 'batch':
            weights -= learning_rate * full_gradient
        elif method == 'mini-batch':
            scaled_gradient = full_gradient * (batch_size / N)
            weights -= learning_rate * scaled_gradient
    return np.round(weights.tolist(), 4)

