def OSA(source: str, target: str) -> int:
    len_s1 = len(source)
    len_s2 = len(target)
    dp = np.full((len_s1 + 1, len_s2 + 1), float('inf'))
    dp[0][0] = 0
    for i in range(len_s1 + 1):
        for j in range(len_s2 + 1):
            if i == 0:
                dp[i][j] = j
            elif j == 0:
                dp[i][j] = i
            else:
                if dp[i][j - 1] + 1 < dp[i + 1][j]:
                    dp[i + 1][j] = dp[i][j - 1] + 1
                if dp[i - 1][j] + 1 < dp[i][j + 1]:
                    dp[i][j + 1] = dp[i - 1][j] + 1
                if dp[i - 1][j - 1] + 1 < dp[i][j + 1]:
                    dp[i][j + 1] = min(dp[i][j + 1], dp[i - 1][j - 1] + 1)
                if i < len_s1 - 1 and j < len_s2 - 1 and (source[i] != target[j]):
                    if dp[i - 1][j - 1] + 1 < dp[i][j + 1]:
                        dp[i][j + 1] = min(dp[i][j + 1], dp[i - 1][j - 1] + 1)
    return dp[len_s1][len_s2]

