    n_cols = R.shape[1]
    for i in range(n_rows):
        p = None
        for j in range(i, n_cols):
            if abs(R[i, j]) > 1e-08:
                p = j
                break
        if p is None:
            continue
        (R[i, :], R[p, :]) = (R[p, :], R[i, :])
        for j in range(i + 1, n_rows):
            factor = R[j, p]
