def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_x = min(x)
    max_x = max(x)
    if min_x == max_x:
        return [0.0 for _ in x]
    normalized = [(val - min_x) / (max_x - min_x) for val in x]
    return [round(num, 4) for num in normalized]

