def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if not p or not q:
        return 0.0
    if len(p) != len(q):
        return 0.0
    total = 0.0
    for (pi, qi) in zip(p, q):
        total += np.sqrt(pi * qi)
    return round(total, 4)

