import numpy as np
import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function and its derivative (sigmoid)."""
    if isinstance(x, (int, float)):
        if deriv:
            return round(1.0 / (1 + np.exp(-x)), 4)
        elif x > 0:
            return round(x + np.log(1 + np.exp(-x)), 4)
        else:
            return round(np.log(1 + np.exp(x)), 4)
    else:
        x = np.array(x)
        if deriv:
            sig = 1 / (1 + np.exp(-x))
            sig_rounded = np.round(sig, 4)
            return sig_rounded.tolist()
        else:
            exp_x = np.exp(x)
            log_val = np.where(x > 0, x + np.log(1 + np.exp(-x)), np.log(1 + exp_x))
            log_val_rounded = np.round(log_val, 4)
            return log_val_rounded.tolist()
assert softplus(np.array([-1, 0, 1])) == [0.3133, 0.6931, 1.3133], "failed: softplus([-1,0,1])"
assert softplus(np.array([-1, 0, 1]), True) == [0.2689, 0.5, 0.7311], "failed: softplus([-1,0,1], deriv=True)"
assert softplus([-20]) == [0.0], "failed: softplus([-20])"
assert softplus([20]) == [20.0], "failed: softplus([20])"
