import numpy as np
from collections import Counter
import math
from collections import defaultdict
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    The function builds an N-gram count table from the *corpus* and returns the total log-probability
    of the *sequence* under the resulting unsmoothed language model. If any required count is zero,
    it returns ``float('-inf')``.

    Args:
        corpus: List of word tokens.
        sequence: List of word tokens to compute the log-probability for.
        N: Order of the N-gram model.

    Returns:
        The total log-probability rounded to 4 decimal places, or ``float('-inf')`` if any required count is zero.
    """
    if N == 0:
        return float('-inf')
    if N == 1:
        ngram_counts = defaultdict(int)
        for word in corpus:
            ngram_counts[word] += 1
    else:
        ngram_counts = defaultdict(lambda : defaultdict(int))
        for i in range(len(corpus) - N + 1):
            current_gram = tuple(corpus[i:i + N])
            prefix = tuple(corpus[i:i + N - 1])
            ngram_counts[prefix][current_gram] += 1
    total = 0.0
    if N == 1:
        total_words = len(corpus)
        for word in sequence:
            if word not in ngram_counts or ngram_counts[word] == 0:
                return float('-inf')
            prob = math.log(ngram_counts[word] / total_words)
            total += prob
    else:
        for i in range(len(sequence) - N + 1):
            window = tuple(sequence[i:i + N])
            prefix = tuple(sequence[i:i + N - 1])
            if prefix not in ngram_counts or window not in ngram_counts[prefix]:
                return float('-inf')
            count_window = ngram_counts[prefix][window]
            count_prefix = sum(ngram_counts[prefix].values())
            if count_window == 0 or count_prefix == 0:
                return float('-inf')
            prob = math.log(count_window / count_prefix)
            total += prob
    return round(total, 4)
