def binomial_probability(n, k, p):
    if k < 0 or k > n:
        return 0.0
    if k == 0:
        prob = (1 - p) ** n
    elif k == n:
        prob = p ** n
    else:
        prob = math.comb(n, k) * p ** k * (1 - p) ** (n - k)
    return round(prob, 5)
