def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a:
        if not b:
            return []
        else:
            return -1
    else:
        cols_a = len(a[0])
        if cols_a != len(b):
            return -1
        result = []
        for row in a:
            dp = 0
            for i in range(cols_a):
                dp += row[i] * b[i]
            result.append(dp)
        return result
