def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not b:
        return -1
    m = len(a)
    n = len(a[0]) if m > 0 else 0
    p = len(b)
    q = len(b[0]) if p > 0 else 0
    for row in a:
        if len(row) != n:
            return -1
    for row in b:
        if len(row) != q:
            return -1
    if n != p:
        return -1
    result = [[0 for _ in range(q)] for _ in range(m)]
    for i in range(m):
        for j in range(q):
            total = 0
            for k in range(n):
                total += a[i][k] * b[k][j]
            result[i][j] = total
    return result
