def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    m = A.shape[0]
    x_old = np.zeros(m)
    for _ in range(n):
        x_new = np.zeros(m)
        for i in range(m):
            sum_val = 0.0
            for j in range(m):
                if j != i:
                    sum_val += A[i, j] * x_old[j]
            x_new[i] = (b[i] - sum_val) / A[i, i]
        x_old = x_new.copy()
        x_old = np.round(x_old, 4)
    return x_old.tolist()
