from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    target_values = [ex[target_attr] for ex in examples]
    if len(set(target_values)) == 1:
        return {'leaf': target_values[0]}
    if not attributes:
        majority = Counter(target_values).most_common(1)[0][0]
        return {'leaf': majority}
    current_counts = Counter(target_values)
    total = len(examples)
    entropy = 0
    for count in current_counts.values():
        p = count / total
        entropy -= p * math.log2(p)
    max_gain = -1
    best_attr = None
    for attr in attributes:
        groups = {}
        for ex in examples:
            val = ex[attr]
            if val not in groups:
                groups[val] = []
            groups[val].append(ex)
        sum_entropy = 0
        for (val, group) in groups.items():
            group_counts = Counter([ex[target_attr] for ex in group])
            group_total = len(group)
            if group_total == 0:
                continue
            group_entropy = 0
            for count in group_counts.values():
                p = count / group_total
                group_entropy -= p * math.log2(p)
            sum_entropy += group_total / total * group_entropy
        gain = entropy - sum_entropy
        if gain > max_gain:
            max_gain = gain
            best_attr = attr
    groups = {}
    for ex in examples:
        val = ex[best_attr]
        if val not in groups:
            groups[val] = []
        groups[val].append(ex)
    subtree = {}
    new_attrs = [a for a in attributes if a != best_attr]
    for val in groups:
        subset = groups[val]
        subtree_val = learn_decision_tree(subset, new_attrs, target_attr)
        subtree[val] = subtree_val
    return {best_attr: subtree}
