class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        super(Dense, self).__init__()
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None

    def initialize(self):
        if self.input_shape is None:
            raise ValueError('Input shape is not set.')
        input_dim = self.input_shape[0]
        limit = 1.0 / math.sqrt(input_dim)
        self.W = np.random.uniform(-limit, limit, (input_dim, self.n_units))
        self.w0 = np.zeros((self.n_units,))

    def parameters(self):
        return self.W.size + self.w0.size

    def forward_pass(self, X, training):
        self.layer_input = X
        output = X.dot(self.W) + self.w0
        output = output.reshape(-1)
        rounded_output = np.round(output, 4)
        return rounded_output.tolist()

    def backward_pass(self, accum_grad):
        if not self.trainable:
            return accum_grad
        dW = self.layer_input.T.dot(accum_grad)
        dw0 = np.sum(accum_grad, axis=0)
        learning_rate = 0.01
        self.W -= learning_rate * dW
        self.w0 -= learning_rate * dw0
        grad = accum_grad.dot(self.W.T)
        grad = grad.reshape(-1)
        rounded_grad = np.round(grad, 4)
        return rounded_grad.tolist()

    def output_shape(self):
        return (self.n_units,)
