def gaussian_elimination(A, b):
    aug = np.hstack((A, b[:, np.newaxis]))
    n = A.shape[0]
    for i in range(n):
        max_row = i
        for j in range(i, n):
            if abs(aug[j, i]) > abs(aug[max_row, i]):
                max_row = j
        (aug[i], aug[max_row]) = (aug[max_row], aug[i])
        pivot = aug[i, i]
        for j in range(i + 1, n):
            factor = aug[j, i] / pivot
            aug[j] = aug[j] - factor * aug[i]
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        sum_val = 0.0
        for j in range(i + 1, n):
            sum_val += aug[i, j] * x[j]
        x[i] = (aug[i, n] - sum_val) / aug[i, i]
    x = np.round(x, 4)
    return x.tolist()
