from collections import Counter
def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    unique_query_terms = set(query)
    df = {term: 0 for term in unique_query_terms}
    for term in unique_query_terms:
        for doc in corpus:
            if term in doc:
                df[term] += 1
    N = len(corpus)
    idf = {}
    for term in unique_query_terms:
        numerator = N + 1
        denominator = df[term] + 1
        idf_val = np.log(numerator / denominator)
        idf[term] = idf_val
    idf_list = [idf[term] for term in query]
    result = []
    for doc in corpus:
        if not doc:
            scores = [0.0] * len(query)
        else:
            counter = Counter(doc)
            tf_list = []
            for term in query:
                count = counter.get(term, 0)
                tf = count / len(doc)
                tf_list.append(tf)
            tf_array = np.array(tf_list)
            tfidf_array = tf_array * idf_list
            rounded = tfidf_array.round(4)
            scores = rounded.tolist()
        result.append(scores)
    return result
