def r_squared(y_true, y_pred):
    y_bar = np.mean(y_true)
    sst = np.sum((y_true - y_bar) ** 2)
    sse = np.sum((y_true - y_pred) ** 2)
    epsilon = 1e-10
    if abs(sst) < epsilon:
        if abs(sse) < epsilon:
            return round(1.0, 3)
        else:
            return round(0.0, 3)
    else:
        r_sq = 1 - sse / sst
        return round(r_sq, 3)
