    if not img:
        return -1
    num_cols = len(img[0])
    for row in img:
        if len(row) != num_cols:
            return -1
    if num_cols == 0:
        return -1
    for row in img:
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
    total = 0
    for row in img:
        total += sum(row)
    total_pixels = len(img) * num_cols
    average = total / total_pixels
    return round(average, 2)
