        if len(generated_tokens) >= hparams['n_ctx']:
            input_tokens = generated_tokens[-hparams['n_ctx']:]
        else:
            input_tokens = generated_tokens + [0] * (hparams['n_ctx'] - len(generated_tokens))
        token_embeddings = np.array([params['wte'][t] for t in input_tokens])
        positional_embeddings = params['wpe'][:len(input_tokens)]
        embeddings = token_embeddings + positional_embeddings
        mean = np.mean(embeddings, axis=-1, keepdims=True)
        var = np.var(embeddings, axis=-1, keepdims=True)
        x_normalized = (embeddings - mean) / np.sqrt(var + 1e-08) * params['ln_f']['g'] + params['ln_f']['b']
        logits = np.dot(x_normalized, params['wte'].T)
        last_logits = logits[-1]
        next_token = np.argmax(last_logits)
        generated_tokens.append(next_token)
    return encoder.decode(generated_tokens)
np.random.seed(42)
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
