def feature_scaling(data: np.ndarray) -> (list[list[float]], list[list[float]]):

    def standardize(column):
        mean = np.mean(column)
        std = np.std(column)
        if std == 0:
            return np.zeros_like(column)
        return (column - mean) / std

    def minmax(column):
        min_val = np.min(column)
        max_val = np.max(column)
        if max_val == min_val:
            return np.zeros_like(column)
        return (column - min_val) / (max_val - min_val)
    columns_standard = []
    for i in range(data.shape[1]):
        col = data[:, i]
        scaled_col = standardize(col)
        scaled_col_rounded = np.round(scaled_col, 4)
        columns_standard.append(scaled_col_rounded.tolist())
    scaled_standard = [list(row) for row in zip(*columns_standard)]
    columns_minmax = []
    for i in range(data.shape[1]):
        col = data[:, i]
        scaled_col = minmax(col)
        scaled_col_rounded = np.round(scaled_col, 4)
        columns_minmax.append(scaled_col_rounded.tolist())
    scaled_minmax = [list(row) for row in zip(*columns_minmax)]
