def polynomial_features(X, degree):
    if degree < 0:
        return []
    n_samples = X.shape[0]
    n_features = X.shape[1]
    result = []
    for d in range(0, degree + 1):
        if d == 0:
            new_feature = np.ones((n_samples, 1))
            result.append(new_feature)
        else:
            for combo in combinations_with_replacement(range(n_features), d):
                exponents = [0] * n_features
                for idx in combo:
                    exponents[idx] += 1
                new_feature = np.ones(n_samples)
                for i in range(n_samples):
                    row = X[i]
                    product = 1
                    for j in range(n_features):
                        product *= row[j] ** exponents[j]
                    new_feature[i] = product
                new_feature = new_feature.reshape(-1, 1)
                result.append(new_feature)
    if not result:
        return []
    final_features = np.concatenate(result, axis=1)
    return final_features.tolist()

