def rref(matrix):
    mat = np.array(matrix, dtype=np.float64)
    rows = mat.shape[0]
    cols = mat.shape[1]
    lead = 0
    for r in range(rows):
        if lead >= cols:
            break
        i = r
        while i < rows and mat[i, lead] == 0:
            i += 1
        if i == rows:
            lead += 1
            r -= 1
            continue
        mat[[r, i]] = mat[[i, r]]
        pivot = mat[r, lead]
        if pivot != 0:
            mat[r] = mat[r] / pivot
        for i in range(rows):
            if i != r:
                factor = mat[i, lead]
                mat[i] = mat[i] - factor * mat[r]
        lead += 1
    return mat.tolist()

