def OSA(source: str, target: str) -> int:
    m = len(source)
    n = len(target)
    dp = np.zeros((m + 1, n + 1), dtype=int)
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if source[i - 1] == target[j - 1]:
                sub_cost = dp[i - 1][j - 1]
            else:
                sub_cost = dp[i - 1][j - 1] + 1
            del_cost = dp[i - 1][j] + 1
            ins_cost = dp[i][j - 1] + 1
            trans_cost = float('inf')
            if i >= 2 and j >= 2:
                if source[i - 2] == target[j - 1] and source[i - 1] == target[j - 2]:
                    trans_cost = dp[i - 2][j - 2] + 1
            dp[i][j] = min(sub_cost, del_cost, ins_cost, trans_cost)
    return dp[m][n]

