def gauss_seidel(A, b, n, x_ini=None):
    if x_ini is None:
        x = np.zeros(len(b), dtype=np.float64)
    else:
        x = np.array(x_ini, dtype=np.float64)
    for _ in range(n):
        x_old = x.copy()
        for i in range(len(x)):
            sum_less = 0.0
            for j in range(i):
                sum_less += A[i, j] * x[j]
            sum_more = 0.0
            for j in range(i + 1, len(x)):
                sum_more += A[i, j] * x_old[j]
            denominator = A[i, i]
            if denominator == 0:
                raise ValueError('Division by zero encountered in Gauss-Seidel method.')
