def performance_metrics(actual: list[int], predicted: list[int]) -> tuple:
    pairs = list(zip(actual, predicted))
    counts = Counter(pairs)
    tn = counts.get((0, 0), 0)
    fp = counts.get((0, 1), 0)
    fn = counts.get((1, 0), 0)
    tp = counts.get((1, 1), 0)
    confusion_matrix = [[tn, fp], [fn, tp]]
    total = len(actual)
    if total == 0:
        accuracy = 0.0
    else:
        accuracy = (tp + tn) / total
    precision = tp / (tp + fp) if tp + fp != 0 else 0.0
    recall = tp / (tp + fn) if tp + fn != 0 else 0.0
    if precision + recall == 0:
        f1_score = 0.0
    else:
        f1_score = 2 * (precision * recall) / (precision + recall)
    denominator_specificity = tn + fp
    if denominator_specificity == 0:
        specificity = 0.0
    else:
        specificity = tn / denominator_specificity
    denominator_npv = tn + fn
    if denominator_npv == 0:
        negative_predictive_value = 0.0
    else:
        negative_predictive_value = tn / denominator_npv
    accuracy = round(accuracy, 3)
    f1_score = round(f1_score, 3)
    specificity = round(specificity, 3)
    negative_predictive_value = round(negative_predictive_value, 3)
    return (confusion_matrix, accuracy, f1_score, specificity, negative_predictive_value)

