            input_tokens = generated_tokens + [0] * (hparams['n_ctx'] - len(generated_tokens))
        token_embeddings = np.array([params['wte'][t] for t in input_tokens])
        positional_embeddings = params['wpe'][:len(input_tokens)]
        embeddings = token_embeddings + positional_embeddings
        mean = np.mean(embeddings, axis=-1, keepdims=True)
        var = np.var(embeddings, axis=-1, keepdims=True)
        x_normalized = (embeddings - mean) / np.sqrt(var + 1e-08) * params['ln_f']['g'] + params['ln_f']['b']
        logits = np.dot(x_normalized, params['wte'].T)
        last_logits = logits[-1]
        next_token = np.argmax(last_logits)
        generated_tokens.append(next_token)
    return encoder.decode(generated_tokens)

import numpy as np
def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        exp_values = np.exp(values - np.max(values))
        return exp_values / exp_values.sum()
    result = []
    for i in range(n):
        scores = []
        for j in range(n):
            dot_product = sum((a * b for (a, b) in zip(crystal_values[i], crystal_values[j])))
            scores.append(dot_product)
        weights = softmax(scores)
        weighted_sum = [0.0] * dimension
        for j in range(n):
            weight = weights[j]
            for k in range(dimension):
                weighted_sum[k] += crystal_values[j][k] * weight
        output = sum(weighted_sum)
        result.append(round(output, 4))
    return result

import numpy as np
from collections import Counter
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    docs_terms = [Counter(doc.split()) for doc in corpus]
    L_d_list = [len(doc.split()) for doc in corpus]
    if L_d_list:
        L_avg = sum(L_d_list) / len(L_d_list)
    else:
        L_avg = 0
    term_to_doc_count = {}
    for doc in docs_terms:
        for term in doc:
            term_to_doc_count[term] = term_to_doc_count.get(term, 0) + 1
    query_terms = query.split()
    n_t_dict = {term: term_to_doc_count.get(term, 0) for term in query_terms}
    scores = []
    N = len(corpus)
    for (doc_terms, L_d) in zip(docs_terms, L_d_list):
        score = 0.0
        for term in query_terms:
            if term in doc_terms:
                tf = doc_terms[term]
                numerator = tf * (k1 + 1)
                denominator = tf + k1
                term_score = numerator / denominator
                n_t = n_t_dict[term]
                if n_t == 0:
                    idf = 0.0
                else:
                    idf = np.log((N - n_t + 1) / n_t)
                score += term_score * idf
        if L_avg == 0:
            length_part = 0.0
        else:
            length_part = b * np.log(L_d / L_avg)
        total_score = score + length_part
        scores.append(round(total_score, 3))
    return scores

import math
def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x >= 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x), rounded to 4 decimal places.
    """
    if x > 20:
        value = x + math.exp(-x)
    elif x < -20:
        value = math.exp(x)
    else:
        value = math.log(1 + math.exp(x))
    return round(value, 4)

import numpy as np
def layer_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    mean = np.mean(X, axis=2, keepdims=True)
    X_centered = X - mean
    var = np.mean(X_centered ** 2, axis=2, keepdims=True)
    var += epsilon
    std = np.sqrt(var)
    X_normalized = X_centered / std
    result = gamma * X_normalized + beta
    return np.round(result, 5).tolist()

