from collections import Counter
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    if not candidate or not reference:
        return 0.0
    ref_words = reference.split()
    candidate_words = candidate.split()
    ref_counts = Counter(ref_words)
    candidate_counts = Counter(candidate_words)
    correct_matches = 0
    for word in candidate_counts:
        correct_matches += min(ref_counts.get(word, 0), candidate_counts[word])

    def lcs(a, b):
        m = len(a)
        n = len(b)
        dp = [[0] * (n + 1) for _ in range(m + 1)]
        for i in range(m):
            for j in range(n):
                if a[i] == b[j]:
                    dp[i + 1][j + 1] = dp[i][j] + 1
                else:
                    dp[i + 1][j + 1] = max(dp[i + 1][j], dp[i][j + 1])
        return dp[m][n]
    correctly_ordered = lcs(candidate_words, ref_words)
    if correct_matches == 0:
        penalty = 0.0
    else:
        penalty = (correct_matches - correctly_ordered) / correct_matches
    len_candidate = len(candidate_words)
    len_ref = len(ref_words)
    p = correct_matches / len_candidate if len_candidate > 0 else 0.0
    r = correct_matches / len_ref if len_ref > 0 else 0.0
    if alpha + beta == 0:
        f_mean = 0.0
    else:
        f_mean = (alpha * p + beta * r) / (alpha + beta)
    meteor = f_mean - gamma * penalty
    return round(meteor, 3)

def min_max(x: list[int]) -> list[float]:
    min_x = min(x)
    max_x = max(x)
    if max_x == min_x:
        return [0.0 for _ in x]
    else:
        normalized = [(xi - min_x) / (max_x - min_x) for xi in x]
        return [round(num, 4) for num in normalized]

