import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0:
        return 0.0
    sum_bhatta = 0.0
    for (pi, qi) in zip(p, q):
        sum_bhatta += np.sqrt(pi * qi)
    distance = -sum_bhatta
    return round(distance, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    result = []
    for i in range(len(a)):
        result.append(a[i] + b[i])
    return result

def compute_efficiency(n_experts, k_active, d_in, d_out):
    flops_dense = d_in * d_out
    flops_moe = k_active / n_experts * flops_dense
    saving = (flops_dense - flops_moe) / flops_dense * 100
    return round(saving, 1)

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:
    x = start_x
    for _ in range(max_iters):
        current_x = x
        derivative = 4 * current_x ** 3 - 9 * current_x ** 2
        x = current_x - learning_rate * derivative
        if abs(x - current_x) < tolerance:
            break
    return round(x, 4)

