from collections import Counter, deque
from typing import List
class Node:

    def __init__(self, item=None):
        self.item = item
        self.children = {}
        self.count = 0
        self.parent = None
        self.link = None
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    filtered_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not filtered_items:
        return []
    root = Node()
    for transaction in transactions:
        sorted_transaction = sorted(transaction)
        current_node = root
        for item in sorted_transaction:
            if item not in current_node.children:
                new_node = Node(item)
                new_node.parent = current_node
                current_node.children[item] = new_node
                current_node = new_node
            else:
                current_node = current_node.children[item]
                current_node.count += 1
    result = []
    nodes = []
    queue = deque()
    for child in root.children.values():
        queue.append(child)
        nodes.append(child)
    while queue:
        node = queue.popleft()
        for child in node.children.values():
            nodes.append(child)
            queue.append(child)

    def get_path_length(node):
        length = 0
        current = node
        while current.parent is not None:
            length += 1
            current = current.parent
        return length
    nodes.sort(key=lambda x: (get_path_length(x), x.item))

    def get_path(node):
        path = []
        current = node
        while current.parent is not None:
            path.append(current.item)
            current = current.parent
        return path[::-1]

    def collect_suffixes(node):
        suffixes = []
        for (item, child) in node.children.items():
            for (suffix, count) in collect_suffixes(child):
                suffixes.append(([item] + suffix, count))
            suffixes.append(([item], child.count))
        return suffixes
    for node in nodes:
        if node.count >= min_support:
            path = get_path(node)
            result.append(path)
            suffixes = collect_suffixes(node)
            for (suffix, count) in suffixes:
                if count >= min_support:
                    combined = path + suffix
                    result.append(combined)
    unique_result = []
    seen = set()
    for itemset in result:
        t = tuple(itemset)
        if t not in seen:
            seen.add(t)
            unique_result.append(itemset)
    for itemset in unique_result:
        itemset.sort()
    unique_result.sort(key=lambda x: (len(x), x))
    return unique_result
