from dataclasses import dataclass
@dataclass
class Leaf:
    value: object
@dataclass
class Node:
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        a_val = tree_a.value
        b_val = tree_b.value
        if not isinstance(a_val, np.ndarray):
            a_val = np.array(a_val)
        if not isinstance(b_val, np.ndarray):
            b_val = np.array(b_val)
        return np.allclose(a_val, b_val, atol=1e-08)
    elif isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        left_equal = compare_trees(tree_a.left, tree_b.left)
        right_equal = compare_trees(tree_a.right, tree_b.right)
        return left_equal and right_equal
    else:
        return False
