    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not isinstance(X, list) or not all((isinstance(row, list) for row in X)):
        return -1
    if not X:
        return -1
    C = len(X[0])
    for row in X:
        if len(row) != C:
            return -1
    if Y is not None:
        if not isinstance(Y, list) or not all((isinstance(row, list) for row in Y)):
            return -1
        if not Y:
            return -1
        if len(Y[0]) != C:
            return -1
    if sigma is None:
        sigma_val = np.sqrt(C / 2)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_val = [sigma] * C
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        for s in sigma:
            if not isinstance(s, (int, float)) or s <= 0:
                return -1
        sigma_val = sigma
    else:
        return -1
    X_np = np.array(X)
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y)
    diff = X_np[:, np.newaxis] - Y_np
    sigma_np = np.array(sigma_val).reshape(1, 1, -1)
    diff_scaled = diff / sigma_np
    squared_diff = diff_scaled ** 2
    sum_squared = np.sum(squared_diff, axis=2)
    kernel = np.exp(-0.5 * sum_squared)
    kernel_rounded = np.round(kernel, 4)
    result = kernel_rounded.tolist()
    return result
